void cps_state::init_dinoeh()
{
	m_maincpu->space(AS_PROGRAM).install_write_handler(0x800180, 0x800181, write16_delegate(*this, FUNC(cps_state::dinoh_sound_command_w)));
	kabuki_setup(dino_decode);
	init_cps1();
}

